mapper.select:
SELECT COUNT(*) FROM log_history
WHERE employee_id = #{employee_id}
  AND operation_type = #{operation_type}
  AND update_date >= date_trunc('day', CAST(#{update_date} AS timestamp))
  AND update_date < (date_trunc('day', CAST(#{update_date} AS timestamp)) + INTERVAL '1 day')

mapper.delete:
DELETE FROM log_history
WHERE employee_id IN

    #{id}
